import React, { useState, useEffect } from "react";
import styles from "./Carousel.module.scss";
import Slider from "./Slider";
import cx from "classnames";

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [direction, setDirection] = useState(null);
  const [shifting, setShifting] = useState(false);

  const track = React.createRef();

  const slideArr = [0, 1, 2, 3];
  const upperLimit = slideArr.length - 1;

  const handleAnimation = direction => {
    console.log(direction);
    directions = direction;
  };

  const handleClick = direction => {
    cloneSlides();
    let newSlideNumber = currentSlide + direction;
    direction = direction;
    setShifting(true);

    setCurrentSlide(newSlideNumber);
    setDirection(direction);
  };

  const resetSlide = () => {
    console.log(currentSlide, "currentslide");
    console.log(upperLimit, "upperLimit");
    if (currentSlide === upperLimit) {
      setShifting(false);
      setCurrentSlide(0);
    }
    if (currentSlide === -4) {
      setShifting(false);
      setCurrentSlide(0);
    }
  };

  const cloneSlides = () => {
    let children = [];
    if (currentSlide === 1 || currentSlide === 2 || currentSlide === 3) {
      let newChild = document.createElement("div");
      newChild.className = `${styles.box}`;
      newChild.innerHTML = `${slideArr[currentSlide - 1]}`;
      track.current.appendChild(newChild);

      // for (let i = 0; i < currentSlide - 1; i++) {
      //   children.push(<div className={styles.box}>{slideArr[i]}</div>);
      // }
      // return children;
    }
    // else if (currentSlide === -1 || currentSlide === -2 || currentSlide === -3) {
    //   for (let i = 0; i < upperLimit; i++) {
    //     children.push(<div className={styles.box}>{slideArr[i]}</div>);
    //   }
    //   return children;
    // }
  };

  const className = cx(styles.track, {
    [styles.shifting]: shifting
  });

  return (
    <div className={styles.mainContainer}>
      <div>CurrentSlide:{currentSlide}</div>
      <div>direction:{direction}</div>
      <div className={styles.bigContainer}>
        <div
          className={className}
          style={{ transform: `translateX(${currentSlide * -152}px)` }}
          ref={track}
          onTransitionEnd={resetSlide}
        >
          {/* {direction === -1 && shifting && cloneSlides()} */}
          <div className={styles.box}>{slideArr[0]}</div>
          <div className={styles.box}> {slideArr[1]} </div>
          <div className={styles.box}>{slideArr[2]}</div>
          <div className={styles.box}>{slideArr[3]}</div>
          {/* {direction === 1 && shifting && cloneSlides()} */}
        </div>
      </div>
      <Slider {...{ setCurrentSlide, currentSlide, handleAnimation, handleClick }} />
    </div>
  );
};

export default Carousel;
