      // track.current.appendChild(newChild);

      for (let i = 0; i < upperLimit; i++) {
        children.push(<div className={styles.box}>{slideArr[i]}</div>);
      }
      return children;
    } else {
      for (let i = 0; i < upperLimit; i++) {
        children.push(<div className={styles.box}>{slideArr[i]}</div>);
      }
      return children;
    }
  };

  const className = cx(styles.track, {
    [styles.shifting]: shifting
  });

  return (
    <div className={styles.mainContainer}>
      <div>CurrentSlide:{currentSlide}</div>
      <div>direction:{direction}</div>
      <div className={styles.bigContainer}>
        <div
          className={className}
          style={{ transform: `translateX(${currentSlide * -152}px)` }}
          ref={track}
          onTransitionEnd={resetSlide}
