import styles from "./Slider.module.css";

const Slider = ({ currentSlide, setCurrentSlide, upperLimit }) => {
  const handleClick = direction => {
    let newSlide = currentSlide;
    console.log(newSlide);
    console.log(upperLimit);
    if (newSlide >= 0 || newSlide <= upperLimit) {
      direction == "previous" ? (newSlide = upperLimit) : (newSlide = 0);
    } else {
      direction == "previous" ? (newSlide -= 1) : (newSlide += 1);
    }
    setCurrentSlide(newSlide);
  };

  return (
    <div className={styles.sliderContainer}>
      <div className={styles.previous} onClick={() => handleClick("previous")}>
        &#10094;
      </div>
      <div className={styles.next} onClick={() => handleClick("next")}>
        &#10095;
      </div>
    </div>
  );
};

export default Slider;
