import React, { useState, useEffect } from "react";
import styles from "./Carousel.module.css";
import Slider from "./Slider";
import { motion } from "framer-motion";

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [direction, setDirection] = useState(null);
  const upperLimit = 4;

  const handleAnimation = direction => {
    console.log(direction);
  };

  const handleClick = direction => {
    let newSlide = currentSlide;
    setDirection(direction);
    if ((newSlide == 0 && direction == "previous") || (newSlide == upperLimit && direction == "next")) {
      direction == "previous" ? (newSlide = upperLimit) : (newSlide = 0);
    } else {
      direction == "previous" ? (newSlide -= 1) : (newSlide += 1);
    }
    setCurrentSlide(newSlide);
  };

  const variants = {
    previous: { opacity: 1, x: [-300, 0] },
    next: { opacity: 1, x: [300, 0] }
  };

  return (
    <div className={styles.mainContainer}>
      <motion.div
        initial={false}
        animate={direction == "previous" ? "previous" : "next"}
        variants={variants}
        className={styles.bigContainer}
      >
        <div className={styles.box}> {currentSlide} </div>
      </motion.div>
      <Slider {...{ setCurrentSlide, currentSlide, handleAnimation, handleClick }} />
    </div>
  );
};

export default Carousel;
