import React, { useState } from "react";
import styles from "./Carousel.module.css";
import Slider from "./Slider";

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  return (
    <div className={styles.mainContainer}>
      <div className={styles.bigContainer}>
        render(
        {currentSlide == 0 ? <div className={styles.box}> 1 </div> : null}
        {currentSlide == 1 ? <div className={styles.box}> 2 </div> : null}
        {currentSlide == 2 ? <div className={styles.box}> 3 </div> : null}
        {currentSlide == 3 ? <div className={styles.box}> 4 </div> : null})
      </div>
      <Slider {...{ setCurrentSlide, currentSlide }} />
    </div>
  );
};

export default Carousel;
