import React, { useState, useEffect } from "react";
import styles from "./Carousel.module.css";
import Slider from "./Slider";
import { motion } from "framer-motion";

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [direction, setDirection] = useState(null);

  const handleAnimation = direction => {
    setDirection(direction);
  };

  const variants = {
    left: { opacity: 1, x: 0 },
    right: { opacity: 0, x: 100 }
  };

  return (
    <div className={styles.mainContainer}>
      <motion.div initial={false} animate={direction ? "left" : "right"} variants={variants} className={styles.bigContainer}>
        <div className={styles.box}> {currentSlide} </div>
      </motion.div>
      <Slider {...{ setCurrentSlide, currentSlide, handleAnimation }} upperLimit={4} />
    </div>
  );
};

export default Carousel;
